import React from "react";

export default function BollywoodStrikeBoard({ strikesLeft }) {
  const fullWord = "BOLLYWOOD".split("");
  const totalStrikes = 9 - strikesLeft;

  return (
    <div style={{ margin: "1rem 0", textAlign: "center" }}>
      <div style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>
        {fullWord.map((letter, i) => (
          <span
            key={i}
            style={{
              margin: "0 0.3rem",
              textDecoration: i < totalStrikes ? "line-through" : "none",
              color: i < totalStrikes ? "#ff4d4d" : "#fff",
              transition: "all 0.3s ease",
              opacity: i < totalStrikes ? "0.6" : "1"
            }}
          >
            {letter}
          </span>
        ))}
      </div>
      <div style={{ color: strikesLeft <= 3 ? "#ff4d4d" : "#fff" }}>
        {strikesLeft} chances left
      </div>
    </div>
  );
}
